document.addEventListener("DOMContentLoaded",function(){const elementsToAnimate=document.querySelectorAll(".fade-in");const observerOptions={threshold:.1};const observer=new IntersectionObserver((entries,observer)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add("visible");observer.unobserve(entry.target)}})},observerOptions);elementsToAnimate.forEach(element=>{observer.observe(element)});function toggleMenu(){const menu=document.querySelector(".menu");const menuIcon=document.querySelector(".menu-icon");menu.classList.toggle("open");menuIcon.classList.toggle("open")}const menuIcon=document.querySelector(".menu-icon");if(menuIcon){menuIcon.addEventListener("click",toggleMenu)}});